import React/* , { useEffect }  */from 'react'
import Card from './citiesCards'

/* import { useState } from 'react' */
import axios from 'axios'
import { useState, useEffect } from 'react'
function Cards() {
  const [filter, setFilter] = useState('');
  const [ciudades, setCiudades] = useState([]);

  useEffect (() => {
    const loadDate = async () =>{
      const response = await axios.get(`http://localhost:4000/api/allcities`)
      console.log(response.data.response.cities)
      setCiudades(response.data.response.cities)
      loadDate();
    }
  
  },[])
  
  return (
    <div className='cities'>
      <input className="inp-search" type="text" placeholder="Search..." value={filter} onChange={(event)=>setFilter(event.target.value)}/>
      <div className='container d-flex justify-content-center h-100'>
        <div className='row cards'>
           {
             ciudades.length!== 0? (
               ciudades.map((city) => (
               <Card
                  image = {city.image}
                  continent = {city}
                  name = {city.name}
                  description = {city.description}
               />
             )))
             : (<h2>------Not Found  -----</h2>)
                }
        </div>
    </div>
    </div>
    
  )
}

export default Cards
