import React from 'react';
import { styled } from '@mui/material/styles';
import { useParams } from 'react-router-dom';
import { useState, useEffect } from ' react';
import itinerariesActions from '../redux/actions/itinerariesActions';
import { connect } from "react-redux";
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import IconButton from '@mui/material/IconButton';
import Card from '@mui/material/Card';
import CardHeader from '@mui/material/CardHeader';
import CardMedia from '@mui/material/CardMedia';
import CardContent from '@mui/material/CardContent';
import CardActions from '@mui/material/CardActions';
import Collapse from '@mui/material/Collapse';




const ExpandMore = styled((props) => {
  /* console.log(props) */
  const { expand, ...other } = props;
  return <IconButton {...other} />;
})(({ theme, expand }) => ({
  transform: !expand ? 'rotate(0deg)' : 'rotate(180deg)',
  marginLeft: 'auto',
  transition: theme.transitions.create('transform', {
    duration: theme.transitions.duration.shortest,
  }),
}));


const DetailsItinerary = (props) => {

        const [expanded, setExpanded] = React.useState(false);
        const handleExpandClick = () => {
          setExpanded(!expanded);
        };
        const {id} = useParams()

        const [inputText, setInputText] = useState("")
        const [modify, setModify] = useState(false)
        const [reload, setReload] = useState(false)
        const [likes, setLikes] = useState(props.itinerary.likes)

        useEffect(() => {

          props.getOneItinerary(props.id)
          .then( response => setLikes(response.data.response.likes))
    // eslint-disable-next-line react-hooks/exhaustive-deps
          }, [!reload]);

          /* useEffect(()=> {

            props.activityPerItinerary(props.id).then(
              res=>{setActivities(res.response)}

            )
            // eslint-disable-next-line react-hooks/exhaustive-deps
          },[]) */

          async function likesOrDislikes() {
            await props.likeDislike(props.id)
            setReload(!reload)
           }

        console.log(props)
        return (
          <>
          <div className="container div-itinerary">
              <div className="img-title-itinerary">
                <h1>{props.name}</h1>
                <img className='img-itinerary' src={props.image} alt="rover" />
              </div>
            <div className="card card-itinerary">
              <p className="description-itinerary">{props.description}</p>
              <div className="cont-img-info"></div>
              <div className="img-p-itinerary">
                <img className="userImg" src={props.imageUser}></img>
                <p>{props.userName}</p>
              </div>
              <div>
                <p>{props.hashtags}</p>
                <p>Price: {"ðŸ’¸".repeat(parseInt(props.price))}</p>
                <p>{props.hashtags}</p>
                <p>Duration: {props.hours} Hours</p>
                <div>{/* TERNARIO DE LIKES */}
                  <div>
                  <CardActions disableSpacing>


                    <div className="likeDislike d-flex">

                                {props.user ?
                                  (<button onClick={likesOrDislikes}>{likes.includes(props.user.id) ?
                                    <span style={{ color: "red", fontSize:30 }} className="material-icons">favorite</span> :
                                    <span style={{  fontSize:30 }}className="material-icons">favorite_border</span>}</button>)

                                  : (<span style={{  fontSize:30 }} className="material-icons">favorite_border</span>)}

                                {<h3 style={{  color:"black ",fontSize:30 }}>{likes.length}</h3>}
                    </div>

                    {/*  <IconButton aria-label="add to favorites">
                        <FavoriteIcon />
                      </IconButton> */}

                      <ExpandMore
                        expand={expanded}
                        onClick={handleExpandClick}
                        aria-expanded={expanded}
                        aria-label="show more"
                      >
                        <ExpandMoreIcon />
                      </ExpandMore>
                    </CardActions>
                  </div>
                </div>
              </div>
              <p>
                <a className="btn btn-primary" data-bs-toggle="collapse" href="#collapseExample" role="button" aria-expanded="false" aria-controls="collapseExample">
                  View More
                </a>
              </p>
              <div className="collapse" id="collapseExample">
                <div className="card card-body">
                  {/* ACTIVITIES */}
                </div>
              </div>
            </div>
          </div>
          </>
        );
}

const mapDispatchToProps = {
  findOneCity: citiesActions.findOneCity,
  getOneItinerary:itinerariesActions.getOneItinerary,
  itinerariesPorCiudad: itinerariesActions.itinerariesPorCiudad,
  /* addComment: commentsActions.addComment,
  modifiComment: commentsActions.modifiComment,
  deleteComment: commentsActions.deleteComment, */
  likeDislike: itinerariesActions.likeDislike,
  /* activityPerItinerary:activitiesActions.activityPerItinerary, */

};

const mapStateToProps = (state) => {
return {
    city: state.Data.city,
    itineraries: state.itinerariesReducer.itineraries, 
    user: state.userReducer.user, 
/*     activities: state.activitiesReducer.activities,
 */     
};
};

export default connect(mapStateToProps, mapDispatchToProps)(DetailsItinerary);
