import React, { useRef, useState } from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/grid";
import "swiper/css/pagination";
import "swiper/css/navigation";
import { Grid, Pagination, Autoplay, Navigation } from "swiper";

import "../style.css";
import Datos from "./datos";
/* import { Autoplay, Pagination, Navigation } from "swiper"; */

const Carousel = () => {
  return (
    <div className="divcarousel">
      <Swiper
        slidesPerView={2}
        grid={{
          rows: 2
        }}
        slidesPerGroup={2}
        spaceBetween={30}
        breakpoints={{
          "@0.00": {
            slidesPerView: 2,
            spaceBetween: 10,

          },
          "@0.75": {
            slidesPerView: 2,
            spaceBetween: 20,
          },
          "@1.00": {
            slidesPerView: 3,
            spaceBetween: 40,
          },
          "@1.50": {
            slidesPerView: 4,
            spaceBetween: 50,
          },
        }}
        pagination={{
          clickable: true
        }}
        hashNavigation={{
          watchState: true
        }}
        navigation={true}
        autoplay={{
          delay: 2500,
          disableOnInteraction: false
        }}
        modules={[Grid, Pagination, Autoplay, Navigation]}
        className="mySwiper"
      >
        <SwiperSlide>
            <div className="card" style={{width: 25 + 'rem'}}>
                <img src={process.env.PUBLIC_URL + `/assets/${Datos.image}`} className="card-img-top carousel-img" alt="barcelona"></img>
                <div className="card-body">
                    <h3>{Datos.name}</h3>
                    <p className="card-text">{Datos.description}</p>
                </div>
            </div>
        </SwiperSlide>
      </Swiper>

      {/* <Swiper
        slidesPerView={4}
        slidesPerGroup={4}
        spaceBetween={30}
        breakpoints={{
          "@0.00": {
            slidesPerView: 2,
            spaceBetween: 10,

          },
