import React from 'react'
import "./style.css";
import datos from "./components/datos"
/* import Cards from './components/cards.js' */
import Header from './components/header'
import Footer from './components/footer'
/* import Carousel from './components/carousel' */
import Slider from './components/carousel'
// Import Swiper React components
import { Swiper, SwiperSlide } from "swiper/react";

// Import Swiper styles
import "swiper/css";
import "swiper/css/pagination";

import "./styles.css";

// import required modules
import { Pagination } from "swiper";

function App() {
  return (
    <div>
      <Header />
      {/* <main>
        <Cards />
      </main> */}
      <div>
      <Swiper
        slidesPerView={4}
        slidesPerGroup={4}
        spaceBetween={30}
        breakpoints={{
          "@0.00": {
            slidesPerView: 1,
            spaceBetween: 10,
          },
          "@0.75": {
            slidesPerView: 1,
            spaceBetween: 10,
          },
          "@1.00": {
            slidesPerView: 3,
            spaceBetween: 10,
          },
          "@1.50": {
            slidesPerView: 4,
            spaceBetween: 10,
          },
        }}
        autoplay={{
          delay: 3500,
          disableOnInteraction: false,
        }}
        pagination={{
          clickable: true,
        }}
        navigation={true}
        modules={[Autoplay, Pagination, Navigation]}
        className="mySwiper"
      >
        {Datos.map((datos) => (
          <SwiperSlide>
            <div className="div">
              <img src={datos.image} />
              <h3>{datos.name}</h3>
            </div>
          </SwiperSlide>
        ))}
      </Swiper>
    </div>
      <Footer/>
    </div>
  );
}
export default App
