import React from 'react';
import activitiesActions from '../../redux/actions/activitiesActions';
import commentsActions from '../../Redux/actions/commentsActions';
import ActivityItem from '../../components/activities/activities';
import Comments from '../comments/comments';
import Comment from '../comments/comment';
import { useParams } from 'react-router-dom';
import { useState, useEffect } from 'react';
import itinerariesActions from '../../redux/actions/itinerariesActions';
import { connect } from "react-redux";

const DetailsItinerary = ({itinerary}) => {

        const [expanded, setExpanded] = React.useState(false);
        const handleExpandClick = () => {
          setExpanded(!expanded);
        };

        const {id} = useParams()
        
        const [inputText, setInputText] = useState("")
        const [modify, setModify] = useState(false)
        const [reload, setReload] = useState(false)
        const [likes, setLikes] = useState(itinerary.itinerary.likes)
        const[activities, setActivities] = useState([])

        useEffect(() => {
        
          itinerary.getOneItinerary(itinerary.id)
          .then( response => setLikes(response.data.response.likes))
    
        }, [!reload]);



        useEffect(()=> {

          itinerary.activityPerItinerary(itinerary.id).then(
            res=>{setActivities(res.response)}

          )
        },[])
        
        async function likesOrDislikes() {
          await itinerary.likeDislike(itinerary.id)
          setReload(!reload)
         }

    
        console.log(itinerary)


        return (
          <>
          <div className="container div-itinerary">
              <div className="img-title-itinerary">
                <h1>{itinerary.name}</h1>
                <img className='img-itinerary' src={itinerary.image} alt="rover" />
              </div>
            <div className="card card-itinerary">
              <p className="description-itinerary">{itinerary.description}</p>
              <div className="cont-img-info"></div>
              <div className="img-p-itinerary">
                <img className="userImg" src={itinerary.imageUser}></img>
                <p>{itinerary.userName}</p>
              </div>
              <div>
                <p>{itinerary.hashtags}</p>
                <p>Price: {"ðŸ’¸".repeat(parseInt(itinerary.price))}</p>
                <p>{itinerary.hashtags}</p>
                <p>Duration: {itinerary.hours} Hours</p>
                <div> 
                  {itinerary.user ?
                          (<button onClick={likesOrDislikes}>{likes.includes(itinerary.user.id) ?
                            <span style={{ color: "red", fontSize:30 }} className="material-icons">favorite</span> :
                            <span style={{  fontSize:30 }}className="material-icons">favorite_border</span>}</button>)

                          : (<span style={{  fontSize:30 }} className="material-icons">favorite_border</span>)}

                        {<h3 style={{  color:"black ",fontSize:30 }}>{likes.length}</h3>}</div>
              </div>
              <p>
                <a class="btn btn-primary" data-bs-toggle="collapse" href="#collapseExample" role="button" aria-expanded="false" aria-controls="collapseExample">
                  View More
                </a>
              </p>
              <div class="collapse" id="collapseExample">
                <div class="card card-body">
                  {/* INSERTE LA ACTIVIDAD */}
                  <h3 className="text-center mb-3">Itinerary Activities</h3>
                    <div className="d-flex justify-content-center ">
                    
                  {activities?.length !== 0 ?  
                  ( 
                    activities.map(activity => (
                      
                          <ActivityItem activity={activity} key={activity._id} />
                        
                          ))):
                          (<div></div>)}
                        </div>

                        {itinerary.itinerary.comments.map((comment) => (
                          <Comments  itineraryId={itinerary.id} commentId={comment._id} comment={comment} key={comment._id}  />
                            ))
                                    
                          }
                          <Comment itineraryId={itinerary.id}/>
                </div>
              </div>
            </div>
          </div>
          </>
        );
}
const mapDispatchToProps = {
  getOneItinerary:itinerariesActions.getOneItinerary,
  itinerariesPerCity: itinerariesActions.itinerariesPerCity,
  addComment: commentsActions.addComment,
  modifiComment: commentsActions.modifiComment,
  deleteComment: commentsActions.deleteComment,
  likeDislike: itinerariesActions.likeDislike,
  activityPerItinerary:activitiesActions.activityPerItinerary,
  
};

const mapStateToProps = (state) => {
return {

    user: state.userReducer.user, 
    activities: state.activitiesReducers.activities,
     
};
};

export default connect(mapStateToProps, mapDispatchToProps)(DetailsItinerary);
