import React from "react";
import { useParams } from 'react-router-dom'
import { useEffect } from 'react'
import { connect } from "react-redux";
import citiesActions from "../redux/actions/citiesActions";
import itinerariesActions from '../redux/actions/itinerariesActions'
import {Link as LinkRouter} from "react-router-dom"
import detailsItinerary from './detailsCard'



const Cards = (props) =>{
    const { 
        city,
        itineraries
    } = props;
 
    const {id} = useParams()
  
    useEffect(() => {
        
        props.findOneCity(id)
        props.itinerariesPerCity(id)
    }, []);

       
    return(
        
        <>
        {props.itineraries?.length !== 0 ?  
        ( 
           itineraries.map(itinerary => (
            
                <detailsItinerary itinerary={itinerary} key={itinerary._id} />
               
                ))):
                (<h3 className='mt-5 title h2 text-center textCarr'>WE COULD NOT FIND ANY ITINERARY FOR THIS CITY</h3>)}
         
         </>
        )
       
    }
    const mapStateToProps = (state) => {
        return {
          cities: state.Data.cities,
          filterCities: state.Data.filterCities,
        };
      };
      const mapDispatchToProps = {
        findOneCity: citiesActions.perCity,
                itinerariesPerCity: itinerariesActions.itinerariesPerCity
      };
      export default connect(mapStateToProps,mapDispatchToProps)(Cards)
