const initialState = {
    city: [],
    aux: []
}

const citiesReducer = (state = initialState, action) => {
    switch (action.type) {
        case 'fetch':
            return {
                ...state,
                itineraries: action.payload,
                aux: action.payload
            }
            case 'deleteCity':
                return {
                    ...state,
                    itineraries: action.payload
                }
            case 'loadCity':
                let itineraries = [...state.itineraries]
                itineraries.push(action.payload)
                return {
                    ...state,
                    itineraries,
                    aux: [...itineraries]
                }
                case 'filtro':
                    const filtrado = action.payload.cities.filter((city => city.name.toLowerCase().startsWith(action.payload.value.toLowerCase().trim())))
        
                    return {
                        ...state,
                        filterCities: filtrado
                    }
                default:
                    return state
            
    }
}
export default citiesReducer
